import React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ExitToAppIcon from '@mui/icons-material/ExitToApp'; // Import logout icon
import { red } from '@mui/material/colors'; // Import red color from MUI
import { useAuth } from '../context/authContext';

export default function PermanentDrawer() {
  const drawerWidth = 240;

  const classes = [
    { name: "Introduction to Biology", icon: <InboxIcon /> },
    { name: "World History", icon: <MailIcon /> },
    { name: "Advanced Mathematics", icon: <InboxIcon /> },
  ];

  const subMenuItems = [
    { name: "Class Resources", icon: <MailIcon /> },
    { name: "Assignments", icon: <InboxIcon /> },
    { name: "Schedule", icon: <MailIcon /> },
  ];

  const handleLogout = async  () => {
    const { logout } = useAuth();
    try {
      await logout();
      console.log("Logged out successfully!");
      // Here you can also redirect the user or manage the state post-logout
      // For example, using history from react-router-dom if you're handling routing
      // history.push('/login');
    } catch (error) {
      console.error("Failed to log out:", error);
    }
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Box sx={{ overflow: 'auto' }}>
          <Typography variant="h6" noWrap component="div" sx={{ p: 2 }}>
            Classes
          </Typography>
          <Divider />
          <List>
            {classes.map((item, index) => (
              <ListItem key={item.name} disablePadding>
                <ListItemButton>
                  <ListItemIcon>{item.icon}</ListItemIcon>
                  <ListItemText primary={item.name} />
                </ListItemButton>
              </ListItem>
            ))}
            <Divider />
            {subMenuItems.map((item, index) => (
              <ListItem key={item.name} disablePadding>
                <ListItemButton>
                  <ListItemIcon>{item.icon}</ListItemIcon>
                  <ListItemText primary={item.name} />
                </ListItemButton>
              </ListItem>
            ))}
            <Divider />
            <ListItem disablePadding>
              <ListItemButton onClick={handleLogout}>
                <ListItemIcon>
                  <ExitToAppIcon sx={{ color: red[500] }} />
                </ListItemIcon>
                <ListItemText primary="Logout" primaryTypographyProps={{ color: red[500] }} />
              </ListItemButton>
            </ListItem>
          </List>
        </Box>
      </Drawer>
    </Box>
  );
}
