import React, { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import { useAuth } from '../context/authContext';

const AddClassDialog = ({ open, handleClose }) => {
  const { currentUser } = useAuth();
  const [className, setClassName] = useState("");
  const [email, setEmail] = useState("");
  const [emails, setEmails] = useState([]);

  const handleAddEmail = () => {
    if (email && !emails.includes(email)) {
      setEmails([...emails, email]);
      setEmail("");
    }
  };

  const handleDeleteEmail = (emailToDelete) => () => {
    setEmails(emails.filter((email) => email !== emailToDelete));
  };

  const resetFields = () => {
    setClassName("");
    setEmail("");
    setEmails([]);
  };

  const handleAddClass = async () => {
    const allEmails = [...emails, currentUser?.email].filter((e, i, a) => a.indexOf(e) === i);

    const classData = {
      classname: className,
      emails: allEmails
    };

    try {
      const response = await fetch('/classes/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(classData)
      });

      if (response.ok) {
        console.log('Class created successfully:', response);
        resetFields();
        handleClose();
      } else {
        const error = await response.json();
        console.error('Failed to create class:', error.message);
      }
    } catch (error) {
      console.error('Error sending data:', error);
    }
  };

  return (
    <Dialog 
      open={open} 
      onClose={() => { resetFields(); handleClose(); }}
      sx={{ '& .MuiDialog-paper': { minWidth: '40vw', maxWidth: '80vw', width: 'auto' } }}
    >
      <DialogTitle>Add New Class/Create Group</DialogTitle>
      <DialogContent>
        <Stack spacing={2}>
          <TextField
            label="Class Name"
            variant="outlined"
            fullWidth
            value={className}
            onChange={(e) => setClassName(e.target.value)}
          />
          <TextField
            label="Add User by Email"
            variant="outlined"
            fullWidth
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleAddEmail()}
          />
          <Stack direction="row" spacing={1}>
            {emails.map((email, index) => (
              <Chip
                key={index}
                label={email}
                onDelete={handleDeleteEmail(email)}
                variant="outlined"
              />
            ))}
          </Stack>
        </Stack>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => { resetFields(); handleClose(); }}>Cancel</Button>
        <Button onClick={handleAddClass}>Add</Button>
      </DialogActions>
    </Dialog>
  );
};

export default AddClassDialog;
