import React from 'react';
import { useAuth } from '../context/authContext';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import { red } from '@mui/material/colors';

export default function PermanentDrawer() {
  const drawerWidth = 240;
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    await logout();
    navigate('/');
  };

  return (
    <Box sx={{ display: 'flex', height: '100vh' }}> {/* Ensure full viewport height */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Box sx={{ overflow: 'auto' }}>
          <Typography variant="h6" noWrap component="div" sx={{ p: 2 }}>
            Classes
          </Typography>
          <Divider />
          <List>
            {/* List items can be mapped here */}
            <ListItem disablePadding>
              <ListItemButton onClick={handleLogout}>
                <ListItemIcon>
                  <ExitToAppIcon sx={{ color: red[500] }} />
                </ListItemIcon>
                <ListItemText primary="Logout" primaryTypographyProps={{ color: red[500] }} />
              </ListItemButton>
            </ListItem>
          </List>
        </Box>
      </Drawer>
      <Box sx={{ flexGrow: 1, bgcolor: 'black', p: 3 }}> {/* Main content area */}
        {/* Dynamic content can be added here */}
        <Typography variant="h4" sx={{ color: 'white' }}>Welcome to the Dashboard</Typography>
      </Box>
    </Box>
  );
}
