import React, { useState } from 'react';
import { TextField, Button, Box, Typography } from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import { useAuth } from '../context/AuthContext'; // Adjust the path as necessary

const MyNotes = () => {
  const [note, setNote] = useState('');
  const { currentUser } = useAuth(); // Get current user from context

  const handleSave = () => {
    if (currentUser) {
      console.log('Note saved:', note);
      console.log('User ID:', currentUser.uid); // You can access user details
      // Here you would typically add functionality to save the note to a database
      // Make sure to handle this operation securely on the backend to verify the user token
    } else {
      console.log('No user logged in');
      // Optionally, prompt the user to log in
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 4 }}>
      <Typography variant="h3" sx={{ fontFamily: 'Cursive', mb: 4, textAlign: 'center' }}>
        My Notes
      </Typography>
      <TextField
        multiline
        rows={10}
        variant="outlined"
        placeholder="Write your notes here..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
        fullWidth
        sx={{ mb: 2 }}
      />
      <Button
        startIcon={<SaveIcon />}
        variant="contained"
        color="primary"
        onClick={handleSave}
        sx={{ width: '200px' }}
      >
        Save
      </Button>
    </Box>
  );
}

export default MyNotes;
