import React, { useState } from 'react';
import { TextField, Button, Box, Typography } from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import { useAuth } from '../context/authContext';
import { useParams } from 'react-router-dom'; // Import useParams

const MyNotes = () => {
  const [note, setNote] = useState('');
  const { currentUser } = useAuth();
  const { classId } = useParams(); // Use useParams to access classId

  const handleSave = () => {
    if (currentUser) {
      currentUser.getIdToken().then(token => {
        console.log('JWT Token:', token);
        console.log('Note saved:', classId);

        // Make sure the endpoint matches your backend configuration
        fetch(`http://localhost:4000/notes/${classId}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({ note })
        })
        .then(response => {
          if (response.ok) {
            return response.json();
          }
          throw new Error('Network response was not ok.');
        })
        .then(data => {
          console.log('Success:', data);
        })
        .catch(error => {
          console.error('Error:', error);
        });
      }).catch(error => {
        console.error('Error getting token:', error);
      });
    } else {
      console.log('No user logged in');
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 4 }}>
      <Typography variant="h3" sx={{ fontFamily: 'Cursive', mb: 4, textAlign: 'center' }}>
        My Notes
      </Typography>
      <TextField
        multiline
        rows={10}
        variant="outlined"
        placeholder="Write your notes here..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
        fullWidth
        sx={{ mb: 2 }}
      />
      <Button
        startIcon={<SaveIcon />}
        variant="contained"
        color="primary"
        onClick={handleSave}
        sx={{ width: '200px' }}
      >
        Save
      </Button>
    </Box>
  );
}

export default MyNotes;
