import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/login';
import { AuthProvider, useAuth } from './context/authContext'; // Assuming useAuth is a hook to access auth context
import CustomSidebar from './pages/sidebar';

// A simple auth check function that can redirect if not logged in
function PrivateRoute({ children }) {
  const { currentUser } = useAuth(); // get the user from context
  return currentUser ? children : <Navigate to="/" />;
}

function App() {
  return (
    <Router>
      <AuthProvider>
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/dashboard" element={
            <PrivateRoute>
              <CustomSidebar />
            </PrivateRoute>
          } />
        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
