import React, { useState, useContext } from 'react';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import { useAuth } from '../context/authContext'; // Import your user context

const AddClassDialog = ({ open, handleClose }) => {
  const { currentUser } = useContext(useAuth); // Assume currentUser contains user data
  const [className, setClassName] = useState("");
  const [email, setEmail] = useState("");
  const [emails, setEmails] = useState([]);

  const handleAddEmail = () => {
    if (email && !emails.includes(email)) {  // Prevent adding duplicates
      setEmails([...emails, email]);
      setEmail("");  // Clear input after adding
    }
  };

  const handleDeleteEmail = (emailToDelete) => () => {
    setEmails(emails.filter((email) => email !== emailToDelete));
  };

  const handleAddClass = async () => {
    const allEmails = [...emails, currentUser.email]; // Include the current user's email
    const classData = {
      classname: className,
      emails: allEmails
    };
    console.log(allEmails);
    try {
      const response = await fetch('/classes/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(classData)
      });
      const result = await response.json();
      if (response.ok) {
        console.log('Class created successfully:', result);
        handleClose(); // Close dialog on success
      } else {
        console.error('Failed to create class:', result.message);
      }
    } catch (error) {
      console.error('Error creating class:', error);
    }
  };

  return (
    <Dialog 
      open={open} 
      onClose={handleClose}
      sx={{ '& .MuiDialog-paper': { minWidth: '40vw', maxWidth: '80vw', width: 'auto' } }}  // Adjust width here
    >
      <DialogTitle>Add New Class/Create Group</DialogTitle>
      <DialogContent>
        <Stack spacing={2}>
          <TextField
            label="Class Name"
            variant="outlined"
            fullWidth
            value={className}
            onChange={(e) => setClassName(e.target.value)}
          />
          <TextField
            label="Add User by Email"
            variant="outlined"
            fullWidth
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleAddEmail()}
          />
          <Stack direction="row" spacing={1}>
            {emails.map((email, index) => (
              <Chip
                key={index}
                label={email}
                onDelete={handleDeleteEmail(email)}
                variant="outlined"
              />
            ))}
          </Stack>
        </Stack>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose}>Cancel</Button>
        <Button onClick={handleAddClass}>Add</Button>
      </DialogActions>
    </Dialog>
  );
};

export default AddClassDialog;
